    "paragraph": {
     "elements": [
      {
       "startIndex": 46144,
       "endIndex": 46320,
       "textRun": {
        "content": "For one- and two-family dwellings, at least one 125V, 15- or 20-amp receptacle outlet (in addition to those for specific equipment) must be installed in each of the following:\n",
        "textStyle": {
        }
       }
      }
     ],
     "paragraphStyle": {
      "namedStyleType": "NORMAL_TEXT",
      "lineSpacing": 115,
      "direction": "LEFT_TO_RIGHT",
      "avoidWidowAndOrphan": true
     }
    }
